mapper.update:
UPDATE t_shop_bill SET sort_status = 1 WHERE shop_id = #{shopId}
AND amount <= ( SELECT a.total_reward - b.full_amount FROM (SELECT IFNULL(SUM(reward),0.00) total_reward FROM t_shop_bill WHERE shop_id = #{shopId}) a, (SELECT IFNULL(SUM(amount),0.00) full_amount FROM t_shop_bill WHERE shop_id = #{shopId} AND sort_status = 1 ) b )
ORDER BY gmt_create ASC LIMIT 1

mapper.select:
SELECT

FROM t_shop_bill
WHERE shop_id = #{shopId}
AND custom_wechat_user_id = #{weChatUserId}
ORDER BY gmt_create ASC
